mapper.insert:
INSERT INTO merge_holdings (
    holding_id, filing_id, cik, company_name, name_of_issuer, cusip, value, shares
) VALUES (
    #{holdingId}, #{filingId}, #{cik}, #{companyName}, #{nameOfIssuer}, #{cusip}, #{value}, #{shares}
)

mapper.select:
SELECT COUNT(1) > 0
FROM merge_holdings
WHERE filing_id = #{filingId} AND cusip = #{cusip}
